import React, { useState } from 'react';
import { Select, Dropdown, Button, Popover } from 'antd';
import type { MenuProps } from 'antd';
import { SketchPicker } from '@hello-pangea/color-picker';
import cls from './tool.module.less';
const { Option } = Select;
interface IProps {
  handClick: any;
}

const classDefault: string = 'htMiddle htLeft';
const alignHorizontal: any = [
  {
    label: '左对齐',
    icon: 'data:image/svg+xml;base64,',
    value: 'left',
    className: 'htLeft',
    type: 'horizontal',
  },
  {
    label: '居中',
    icon: 'data:image/svg+xml;base64,',
    value: 'center',
    className: 'htCenter',
    type: 'horizontal',
  },
  {
    label: '右对齐',
    icon: 'data:image/svg+xml;base64,',
    value: 'right',
    className: 'htRight',
    type: 'horizontal',
  },
];
const alignVertical: any = [
  {
    label: '顶端对齐',
    icon: 'data:image/svg+xml;base64,',
    value: 'top',
    className: 'htTop',
    type: 'vertical',
  },
  {
    label: '垂直居中',
    icon: 'data:image/svg+xml;base64,',
    value: 'middle',
    className: 'htMiddle',
    type: 'vertical',
  },
  {
    label: '底端对齐',
    icon: 'data:image/svg+xml;base64,',
    value: 'bottom',
    className: 'htBottom',
    type: 'vertical',
  },
];
const fonts: any = [
  {
    label: '宋体',
    value: '宋体',
  },
  {
    label: '微软雅黑',
    value: '微软雅黑',
  },
  {
    label: '黑体',
    value: '黑体',
  },
  {
    label: '仿宋',
    value: '仿宋',
  },
  {
    label: '新宋体',
    value: '新宋体',
  },
  {
    label: '隶书',
    value: '隶书',
  },
  {
    label: '楷体',
    value: '楷体',
  },
  {
    label: '幼圆',
    value: '幼圆',
  },
];
const fontSizes: any = [...Array(100).keys()].slice(9);
const formulasTypes: any = [
  {
    label: '求和',
    value: 'text',
    renderer: 'text',
    editor: 'text',
  },
  {
    label: '平均值',
    value: 'numeric',
    renderer: 'numeric',
    editor: 'numeric',
  },
  {
    label: '技数',
    value: 'date',
    renderer: 'date',
    editor: 'date',
  },
  {
    label: '最大值',
    value: 'date',
    renderer: 'date',
    editor: 'date',
  },
  {
    label: '最小值',
    value: 'date',
    renderer: 'date',
    editor: 'date',
  },
];

const ToolBar: React.FC<IProps> = ({ handClick }: IProps) => {
  const [background, setBackground] = useState<string>();
  const [color, setColor] = useState<string>();
  const [openColor, setOpenColor] = useState(false);
  const [openBackground, setOpenBackground] = useState(false);
  let defaultFontWeight: string = 'normal';
  let fontWeight: string = 'normal';
  let defaultFontStyle: string = 'normal';
  let fontStyle: string = 'normal';
  let defaultTextDecoration: string = 'none';
  let textDecoration: string = 'none';
  // 缩进
  let defaultPaddingLeft: number = 4;
  let paddingLeft: number = 4;

  const onSave = () => {
    handClick('onSave', 'onSave');
  };

  const onPublish = () => { };
  
  const setFontWeight = () => {
    if (fontWeight === 'bold') {
      fontWeight = defaultFontWeight;
    } else {
      fontWeight = 'bold';
    }
    handClick(fontWeight, 'fontWeight');
  };

  const setFontStyle = () => {
    if (fontStyle === 'italic') {
      fontStyle = defaultFontStyle;
    } else {
      fontStyle = 'italic';
    }
    handClick(fontStyle, 'fontStyle');
  };

  const setTextDecoration = () => {
    if (textDecoration === 'underline') {
      textDecoration = defaultTextDecoration;
    } else {
      textDecoration = 'underline';
    }
    handClick(textDecoration, 'setTextDecoration');
  };

  const borderThis = (type: string) => {
    if (type === 'border-none') {
      handClick('none', 'border', type);
    } else {
      handClick('1px solid #000000', 'border', type);
    }
  };

  const setBackgroundColor = () => {
    setOpenBackground(false);
    handClick(background, 'backgroundColor');
  };

  const setColors = () => {
    setOpenColor(false);
    handClick(color, 'color');
  };

  const setClassName = (className: string, classType: string) => {
    handClick(className, 'className', classType);
  };

  const reducePaddingLeft = () => {
    let nowLeft = paddingLeft;
    if (nowLeft > 0) {
      nowLeft = Math.max(defaultPaddingLeft, nowLeft - 24);
    }
    paddingLeft = nowLeft;
    handClick(paddingLeft, 'paddingLeft');
  };

  const addPaddingLeft = () => {
    paddingLeft += 24;
    handClick(paddingLeft, 'paddingLeft');
  };

  const mergeAndCenter = () => {
    handClick('', 'mergeAndCenter');
  };

  const handleChange = (value: any, type: any) => {
    handClick(value, type);
  };

  const items: MenuProps['items'] = [
    {
      key: 'bottom',
      label: (
        <div className={cls['border-item']} onClick={() => borderThis('bottom')}>
          <img src="data:image/svg+xml;base64,"></img>
          <div>下框线</div>
        </div>
      ),
    },
    {
      key: 'top',
      label: (
        <div className={cls['border-item']} onClick={() => borderThis('top')}>
          <img src="data:image/svg+xml;base64,"></img>
          <div>上框线</div>
        </div>
      ),
    },
    {
      key: 'start',
      label: (
        <div className={cls['border-item']} onClick={() => borderThis('start')}>
          <img src="data:image/svg+xml;base64,"></img>
          <div>左框线</div>
        </div>
      ),
    },
    {
      key: 'end',
      label: (
        <div className={cls['border-item']} onClick={() => borderThis('end')}>
          <img src="data:image/svg+xml;base64,"></img>
          <div>右框线</div>
        </div>
      ),
    },
    {
      key: 'border-none',
      label: (
        <div className={cls['border-item']} onClick={() => borderThis('none')}>
          <img src="data:image/svg+xml;base64,"></img>
          <div>无框线</div>
        </div>
      ),
    },
    {
      key: 'border-all',
      label: (
        <div className={cls['border-item']} onClick={() => borderThis('all')}>
          <img src="data:image/svg+xml;base64,"></img>
          <div>所有框线</div>
        </div>
      ),
    },
    {
      key: 'border-outline',
      label: (
        <div className={cls['border-item']} onClick={() => borderThis('border-outline')}>
          <img src="data:image/svg+xml;base64,"></img>
          <div>外侧框线</div>
        </div>
      ),
    },
    {
      key: 'border-outline-2',
      label: (
        <div
          className={cls['border-item']}
          onClick={() => borderThis('border-outline-2')}>
          <img src="data:image/svg+xml;base64,"></img>
          <div>粗外侧框线</div>
        </div>
      ),
    },
  ];

  const content = (
    <div>
      <SketchPicker
        onChange={(color) => {
          setBackground(color.hex);
        }}
      />
      <div className={cls['save-btn']} onClick={setBackgroundColor}>
        确定
      </div>
    </div>
  );

  const colorContent = (
    <div>
      <SketchPicker
        onChange={(color) => {
          setColor(color.hex);
        }}
      />
      <div className={cls['save-btn']} onClick={setColors}>
        确定
      </div>
    </div>
  );

  const handleOpenColor = (newOpen: boolean) => {
    setOpenColor(newOpen);
  };

  const handleOpenBackground = (newOpen: boolean) => {
    setOpenBackground(newOpen);
  };

  return (
    <div className={cls['toolbar-start-tab']}>
      <div className={cls['flex-box']}>
        <div className={cls['row-one']}>
          <Button onClick={onSave}>保存</Button>
        </div>
        <div className={cls['row-two']}>
          <Button onClick={onPublish}>发布</Button>
        </div>
      </div>

      <div className={cls['flex-box']}>
        <div className={cls['row-one']}>
          <Select
            defaultValue="宋体"
            style={{ width: 170 }}
            onChange={(value) => handleChange(value, 'fontFamily')}>
            {fonts.map((item: any) => {
              return (
                <Option key={item.value} value={item.value} types={'fontFamily'}>
                  {item.label}
                </Option>
              );
            })}
          </Select>
          <Select
            defaultValue="12"
            style={{ width: 80, marginLeft: 4 }}
            onChange={(value) => handleChange(value + 'px', 'fontSize')}>
            {fontSizes.map((item: any) => {
              return (
                <Option key={item} value={item}>
                  {item}
                </Option>
              );
            })}
          </Select>
        </div>
        <div className={cls['row-two']}>
          <a
            className={
              fontWeight === defaultFontWeight ? cls['icon-action'] : cls['icon-action']
            }
            title="加粗"
            onClick={setFontWeight}>
            <img
              className={cls['spreadsheet-icon']}
              src={
                'data:image/svg+xml;base64,'
              }
            />
          </a>
          <a
            className={
              fontStyle === defaultFontStyle ? cls['icon-action'] : cls['icon-action']
            }
            title="倾斜"
            onClick={setFontStyle}>
            <img
              className={cls['spreadsheet-icon']}
              src={
                'data:image/svg+xml;base64,'
              }
            />
          </a>
          <a
            className={
              textDecoration === defaultTextDecoration
                ? cls['icon-action']
                : cls['icon-action']
            }
            title="下划线"
            onClick={setTextDecoration}>
            <img
              className={cls['spreadsheet-icon']}
              src={
                'data:image/svg+xml;base64,'
              }
            />
          </a>
          <Dropdown menu={{ items }}>
            <a
              className={
                textDecoration === defaultTextDecoration
                  ? cls['icon-action']
                  : cls['icon-action']
              }
              title="边框">
              <img
                className={cls['spreadsheet-icon']}
                src={
                  'data:image/svg+xml;base64,'
                }
              />
            </a>
          </Dropdown>
          <Popover
            placement="bottom"
            open={openBackground}
            onOpenChange={handleOpenBackground}
            content={content}
            trigger="click">
            <div className={cls['color-dropdown']}>
              <a className={cls['icon-a']} title="背景色">
                <img
                  className={cls['spreadsheet-icon']}
                  src={
                    'data:image/svg+xml;base64,'
                  }
                />
                <div
                  className={cls['indicator']}
                  style={{ background: background }}></div>
              </a>
            </div>
          </Popover>
          <Popover
            placement="bottom"
            open={openColor}
            content={colorContent}
            onOpenChange={handleOpenColor}
            trigger="click">
            <div className={cls['color-dropdown']}>
              <a className={cls['icon-a']} title="字体颜色">
                <div className={cls['icon-x']}>A</div>
                <div className={cls['indicator']} style={{ background: color }}></div>
              </a>
            </div>
          </Popover>
        </div>
        <div className={cls['flex-box-title']}>字体</div>
      </div>
      <div className={cls['flex-box']}>
        <div className={cls['flex-horizontal']}>
          <div className={cls['column-one']}>
            <div className={cls['row-one']}>
              {alignVertical.map((align: any) => {
                return (
                  <a
                    key={align.value}
                    className={
                      classDefault.includes(align.className)
                        ? cls['icon-action']
                        : cls['icon-action']
                    }
                    title={align.label}
                    onClick={() => setClassName(align.className, align.type)}>
                    <img className={cls['spreadsheet-icon']} src={align.icon} />
                  </a>
                );
              })}
            </div>
            <div className={cls['row-two']}>
              {alignHorizontal.map((align: any) => {
                return (
                  <a
                    key={align.value}
                    className={
                      classDefault.includes(align.className)
                        ? cls['icon-action']
                        : cls['icon-action']
                    }
                    title={align.label}
                    onClick={() => setClassName(align.className, align.type)}>
                    <img className={cls['spreadsheet-icon']} src={align.icon} />
                  </a>
                );
              })}
            </div>
          </div>
          <div className={cls['column-two']}>
            <div className={cls['row-one']}></div>
            <div className={cls['row-two']}>
              <a
                className={cls['icon-action']}
                title="减少缩进量"
                onClick={reducePaddingLeft}>
                <img
                  className={cls['spreadsheet-icon']}
                  src={
                    'data:image/svg+xml;base64,'
                  }
                />
              </a>
              <a
                className={cls['icon-action']}
                title="增加缩进量"
                onClick={addPaddingLeft}>
                <img
                  className={cls['spreadsheet-icon']}
                  src={
                    'data:image/svg+xml;base64,'
                  }
                />
              </a>
            </div>
          </div>
          <div className={cls['column-divider']}></div>
          <div className={cls['column-three']}>
            <div className={cls['row-one']}>
              <a className={cls['icon-action']} title="合并居中" onClick={mergeAndCenter}>
                <span>合并居中</span>
              </a>
            </div>
            <div className={cls['row-two']}>
              <a
                className={cls['icon-action']}
                title="拆分单元格"
                onClick={mergeAndCenter}>
                <span>拆分单元格</span>
              </a>
            </div>
          </div>
        </div>
        <div className={cls['flex-box-title']}>对齐方式</div>
      </div>
      <div className={cls['flex-box']}>
        <div className={cls['row-one']}>
          <Select defaultValue="求和" style={{ width: 100 }} onChange={handleChange}>
            {formulasTypes.map((item: any) => {
              return (
                <Option key={item.value} value={item.value}>
                  {item.label}
                </Option>
              );
            })}
          </Select>
        </div>
        <div className={cls['row-two']}></div>
        <div className={cls['flex-box-title']}>公式</div>
      </div>
    </div>
  );
};
export default ToolBar;
